export function hasValueForAxis(value, axis) {
  if (typeof value === "object" && value !== null) {
    return axis in value;
  }
  return false;
}
export function isTuple(value) {
  return Array.isArray(value) && value.length === 2;
}
export function getValueForAxis(value, axis) {
  if (hasValueForAxis(value, axis)) {
    return value[axis];
  }
  return value;
}

// eslint-disable-next-line @typescript-eslint/ban-types
export function isFunction(func) {
  return typeof func === "function";
}
export function isDate(value) {
  return value instanceof Date;
}
export function isKeyValueObject(datum) {
  return typeof datum === "object" && !Array.isArray(datum) && !(datum instanceof Date);
}